mapper.select:
SELECT m.* FROM message m LEFT JOIN message_delete md ON m.id = md.message_id AND md.user_id = #{userId} WHERE md.id IS NULL
AND (m.status >= 0 OR m.from_user_id = #{userId})
AND chat_type = 1 AND ( (from_user_id = #{userId} AND to_id = #{targetId}) OR (from_user_id = #{targetId} AND to_id = #{userId}) )
AND chat_type = 2 AND to_id = #{targetId}
ORDER BY send_time DESC LIMIT #{offset}, #{limit}

mapper.update:
UPDATE message
SET status = #{status}
WHERE id = #{id}

UPDATE message 
SET status = 0, 
    recall_time = CURRENT_TIMESTAMP
WHERE id = #{messageId}
  AND from_user_id = #{fromUserId}
  AND status = 1

mapper.delete:
DELETE FROM message
WHERE from_user_id = #{userId}
  AND to_id = #{targetId}
  AND chat_type = #{chatType}

mapper.update:
UPDATE message
status = #{status}, recall_time = #{recallTime}, persist_status = #{persistStatus},
WHERE id = #{id}

mapper.select:
SELECT m.*
FROM message m
LEFT JOIN message_delete md ON m.id = md.message_id AND md.user_id = #{userId}
WHERE md.id IS NULL
  AND m.status = 1
  AND m.msg_type = 1
  AND m.content LIKE CONCAT('%', #{keyword}, '%')
  AND (

      (m.chat_type = 1 AND (m.from_user_id = #{userId} OR m.to_id = #{userId}))
      OR


          (m.chat_type = 2 AND m.to_id IN

              #{groupId}
          )


          1 = 0

  )




              AND m.chat_type = 1
              AND ((m.from_user_id = #{userId} AND m.to_id = #{targetId})
                   OR (m.from_user_id = #{targetId} AND m.to_id = #{userId}))


              AND m.chat_type = 2
              AND m.to_id = #{targetId}





      AND m.send_time >= #{startTime}


      AND m.send_time <= #{endTime}

ORDER BY m.send_time DESC
LIMIT #{offset}, #{limit}

( SELECT m.* FROM message m LEFT JOIN message_delete md ON m.id = md.message_id AND md.user_id = #{userId} WHERE md.id IS NULL AND m.status >= 0
AND m.chat_type = 1 AND ((m.from_user_id = #{userId} AND m.to_id = #{targetId}) OR (m.from_user_id = #{targetId} AND m.to_id = #{userId}))
AND m.chat_type = 2 AND m.to_id = #{targetId}
AND m.send_time <= (SELECT send_time FROM message WHERE id = #{messageId}) ORDER BY m.send_time DESC LIMIT #{contextSize} ) UNION ALL ( SELECT m.* FROM message m LEFT JOIN message_delete md ON m.id = md.message_id AND md.user_id = #{userId} WHERE md.id IS NULL AND m.status >= 0 AND m.id != #{messageId}
AND m.chat_type = 1 AND ((m.from_user_id = #{userId} AND m.to_id = #{targetId}) OR (m.from_user_id = #{targetId} AND m.to_id = #{userId}))
AND m.chat_type = 2 AND m.to_id = #{targetId}
AND m.send_time > (SELECT send_time FROM message WHERE id = #{messageId}) ORDER BY m.send_time ASC LIMIT #{contextSize} ) ORDER BY send_time DESC

SELECT COUNT(*)
FROM message m
LEFT JOIN message_delete md ON m.id = md.message_id AND md.user_id = #{userId}
WHERE md.id IS NULL
  AND m.status = 1
  AND m.msg_type = 1
  AND m.content LIKE CONCAT('%', #{keyword}, '%')
  AND (
      (m.chat_type = 1 AND (m.from_user_id = #{userId} OR m.to_id = #{userId}))
      OR

          (m.chat_type = 2 AND m.to_id IN

              #{groupId}
          )


          1 = 0

  )



              AND m.chat_type = 1
              AND ((m.from_user_id = #{userId} AND m.to_id = #{targetId})
                   OR (m.from_user_id = #{targetId} AND m.to_id = #{userId}))


              AND m.chat_type = 2
              AND m.to_id = #{targetId}




      AND m.send_time >= #{startTime}


      AND m.send_time <= #{endTime}
