mapper.select:
SELECT 
FROM square_notification
WHERE user_id = #{userId}
ORDER BY create_time DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(1)
FROM square_notification
WHERE user_id = #{userId}

mapper.update:
UPDATE square_notification
SET `read` = 1
WHERE user_id = #{userId}
  AND `read` = 0
  AND id IN

      #{id}
