mapper.insert:
INSERT INTO square_follow (
    follower_id, followee_id, status, create_time, update_time
) VALUES (
    #{followerId}, #{followeeId}, #{status}, #{createTime}, #{updateTime}
)

mapper.update:
UPDATE square_follow
SET status = #{status}, update_time = CURRENT_TIMESTAMP
WHERE follower_id = #{followerId}
  AND followee_id = #{followeeId}

mapper.select:
SELECT id, follower_id, followee_id, status, create_time, update_time
FROM square_follow
WHERE follower_id = #{followerId}
  AND followee_id = #{followeeId}
LIMIT 1
