mapper.insert:
INSERT INTO square_post (
    user_id, title, content, images, video, tags,
    status, audit_status, audit_reason,
    like_count, comment_count, create_time, update_time
) VALUES (
    #{userId}, #{title}, #{content}, #{images}, #{video}, #{tags},
    #{status}, #{auditStatus}, #{auditReason},
    #{likeCount}, #{commentCount}, #{createTime}, #{updateTime}
)

mapper.update:
UPDATE square_post
SET status = 0,
    update_time = CURRENT_TIMESTAMP
WHERE id = #{id}
  AND user_id = #{userId}
  AND status != 0

mapper.select:
SELECT 
FROM square_post
WHERE user_id = #{userId}
  AND status != 0
ORDER BY create_time DESC
LIMIT #{offset}, #{limit}
