mapper.update:
UPDATE square_post
SET status = 0,
    update_time = CURRENT_TIMESTAMP
WHERE id = #{id}
  AND user_id = #{userId}
  AND status != 0

mapper.select:
SELECT FROM square_post WHERE status = 1 AND audit_status = 1
AND ( title LIKE CONCAT('%', #{keyword}, '%') OR content LIKE CONCAT('%', #{keyword}, '%') )
AND (
tags LIKE CONCAT('%\"', #{t}, '\"%')
)
ORDER BY create_time DESC LIMIT #{offset}, #{limit}

SELECT 
FROM square_post
WHERE user_id = #{userId}
  AND status != 0
ORDER BY create_time DESC
LIMIT #{offset}, #{limit}

SELECT FROM square_post WHERE id IN
#{id}
ORDER BY create_time DESC
