mapper.insert:
INSERT INTO user_whitelist (
    user_id,
    friend_id
) VALUES (
    #{userId},
    #{friendId}
)

mapper.delete:
DELETE FROM user_whitelist
WHERE user_id = #{userId}
  AND friend_id = #{friendId}

mapper.select:
SELECT COUNT(*) > 0
FROM user_whitelist
WHERE user_id = #{userId}
  AND friend_id = #{friendId}

SELECT COUNT(*)
FROM user_whitelist
WHERE user_id = #{userId}
